import React, { useState, useEffect } from "react";
import { listCards } from "./../utils/api";
import { Link } from "react-router-dom";

function Card({ deck }) {
    const [cardList, setCardList] = useState([]);

    useEffect(() => {
        async function loadCards() {
            const cards = await listCards(deck.id);
            setCardList(cards.filter((card) => card.deckId === deck.id));
        }
        loadCards();
    }, []);

    return (
        <>
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{deck.name}</h5>
                    <br />
                    <p className="card-text">{deck.description}</p>
                    <Link to={`/decks/${deck.id}`} style={{float:"left"}} className="btn btn-secondary mr-1">View</Link>
                    <Link to={`/decks/${deck.id}/study`} style={{float:"left"}} className="btn btn-primary">Study</Link>
                    <Link to={"/"} style={{float:"right"}} className="btn btn-danger">Delete</Link>
                </div>
            </div>
        </>
    );
}

export default Card;
