import React, { useState, useEffect } from "react";

function Deck({ deck, cardList }) {
    const [cardCount, setCardCount] = useState(0);
    useEffect(() => {
        if (cardList.length > 0) {
            setCardCount(cardList.reduce((total, card) => total + !(deck.id + card.deckId)));
        }
    }, [cardList])
    return (
        <>
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title" style={{float:"left"}}>{deck.name}</h5>
                    <p><span style={{float:"right"}}> {cardCount} </span></p>
                    <p className="card-text">{deck.description}</p>
                    <a style={{float:"left"}} href="#" className="btn btn-secondary mr-1">View</a>
                    <a style={{float:"left"}} href="#" className="btn btn-primary">Study</a>
                    <a style={{float:"right"}} href="#" className="btn btn-danger">Delete</a>
                </div>
            </div>
        </>
    );
}

export default Deck;
