import React, { useState, useEffect } from "react";
import { listCards } from "./../utils/api";

function Deck({ deck }) {
    const [cardList, setCardList] = useState([]);
    const [cardCount, setCardCount] = useState(0);

    useEffect(() => {
        async function loadCards() {
            const cards = await listCards(deck.id);
            setCardList(cards);
        }
        loadCards();
    }, []);

    useEffect(() => {
        if (cardList.length > 0) {
            setCardCount(cardList.reduce((total, card) => {
                if (card.deckId === deck.id) total.value += 1;
                return total;
            }).value);
        }
    }, [cardList]);
    return (
        <>
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title" style={{float:"left"}}>{deck.name}</h5>
                    <p><span style={{float:"right"}}> {cardCount} </span></p>
                    <p className="card-text">{deck.description}</p>
                    <a style={{float:"left"}} href="#" className="btn btn-secondary mr-1">View</a>
                    <a style={{float:"left"}} href="#" className="btn btn-primary">Study</a>
                    <a style={{float:"right"}} href="#" className="btn btn-danger">Delete</a>
                </div>
            </div>
        </>
    );
}

export default Deck;
