import React, { useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import Header from "./Header";
import NotFound from "./NotFound";
import DeckList from "./DeckList";
import Deck from "./Deck";
import Study from "./Study";
import CreateDeck from "./CreateDeck";
import { listDecks, listCards } from "./../utils/api";

function Layout() {
    const [deckList, setDeckList] = useState([]);
    const [cardList, setCardList] = useState([]);

    useEffect(() => {
        async function loadData() {
            setDeckList(await listDecks());
            setDeckList(await listCards());
        }
        loadData();
    }, []);

    return (
        <>
            <Header />
            <div className="container">
                {/* TODO: Implement the screen starting here */}
                <Switch>
                    <Route exact path="/">
                        <button type="button" class="btn btn-secondary">Create Deck</button>
                        <DeckList deckList={deckList} cardList={cardList}/>
                    </Route>
                    <Route path="/decks/:deckId/study">
                        <Study deckList={deckList}/>
                    </Route>
                    <Route path="/decks/new">
                        <CreateDeck />
                    </Route>
                    <Route>
                        <NotFound />
                    </Route>
                </Switch>
            </div>
        </>
    );
}

export default Layout;
