import React, { useState, useEffect } from "react";
import { listCards, deleteDeck } from "./../utils/api";
import { Link } from "react-router-dom";

function Card({ card }) {

    return (
        <>
            <div className="card">
                <div className="card-body">
                    <div className="card-text container">
                        <div className="row">
                            <div className="col">
                                <p>{card.front}</p>
                            </div>
                            <div className="col">
                                <p>{card.back}</p>
                            </div>
                        </div>
                    </div>
                    <Link to={"/"} style={{float:"left"}} className="btn btn-secondary mr-1">Edit</Link>
                    <button style={{float:"right"}} className="btn btn-danger">Delete</button>
                </div>
            </div>
        </>
    );
}

export default Card;
