import React, { useState, useEffect } from "react";
import { listCards, readDeck, deleteDeck } from "./../utils/api";
import { Link, useHistory, useParams, Switch, Route } from "react-router-dom";
import Study from "./Study";

function DeckInfo({ removeDeck }) {
    const params = useParams();
    const history = useHistory();
    const [cardList, setCardList] = useState([]);
    const [deck, setDeck] = useState({});

    function confirmDelete() {
        let confirm = window.confirm("Delete this deck?\n\nYou will not be able to recover it.");
        if (confirm) {
            deleteDeck(deck.id);
            removeDeck(deck.id);
        }
    }

    useEffect(() => {
        async function loadData() {
            const cards = await listCards(params.deckId);
            const deckObj = await readDeck(params.deckId);
            setCardList(cards.filter((card) => card.deckId === deck.id));
            setDeck(deckObj);
        }
        loadData();
    }, [deck]);

    return (
        <>
            <Switch>
                <Route path="/decks/:deckId/study">
                    <Study deckList={deckList}/>
                </Route>
                <Route path="/decks/:deckId">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb">
                            <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                            <li className="breadcrumb-item active" aria-current="page">{deck.name}</li>
                        </ol>
                    </nav>

                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">{deck.name}</h5>
                            <p className="card-text">{deck.description}</p>
                            <Link to={`/decks/${deck.id}`} style={{float:"left"}} className="btn btn-secondary mr-1">Edit</Link>
                            <Link to={`/decks/${deck.id}/study`} style={{float:"left"}} className="btn btn-primary mr-1">Study</Link>
                            <button style={{float:"right"}} className="btn btn-danger" onClick={confirmDelete}>Delete</button>
                        </div>
                    </div>
                </Route>
            </Switch>
        </>
    )
}

export default Card;
